import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import {
  BarChart, Bar, XAxis
} from 'recharts';

const data = [
  {
    name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
  },
  {
    name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
  },
  {
    name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
  },
  {
    name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
  },
  {
    name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
  },
  {
    name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
  },
  {
    name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
  },
];
const renderLineChart = () => (
      <BarChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <XAxis dataKey="name" />
        <Bar dataKey="uv" fill="#82ca9d" />
      </BarChart>
  );


const Index = (props) => (
  <div>
    {renderLineChart()}

    <h1>Batman TV Shows</h1>
    {props.totalXP}
    <ul>
      {props.shows.map(show => (
        <li key={show.id}>
          <Link as={`/p/${show.id}`} href={`/post?id=${show.id}`}>
            <a>{show.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </div>
)

Index.getInitialProps = async function() {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman')
  const data = await res.json()

  const userNames = ['riginding', 'madfist', 'theanswer']
  const temp = await Promise.all(userNames.map(user => fetch(`https://codestats.net/api/users/${user}`)))
  const data2 = temp.map(async(x) => { return await x.to_json})
  console.log('d', data2)
  console.log('t', temp)

  return {
    shows: data.map(entry => entry.show),
    totalXP: data2.map(x=> x.total_px),
    options: {
        chart: {
          id: 'apexchart-example'
        },
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998]
        }
    },
    series: [{
        name: 'series-1',
        data: [30, 40, 45, 50, 49, 60, 70, 91]
    }]
  }
}

export default Index
