import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import {
  BarChart, Bar, XAxis
} from 'recharts';

const data = [
  {
    name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
  },
  {
    name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
  },
  {
    name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
  },
  {
    name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
  },
  {
    name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
  },
  {
    name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
  },
  {
    name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
  },
];
const renderLineChart = () => (
      <BarChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <XAxis dataKey="name" />
        <Bar dataKey="uv" fill="#82ca9d" />
      </BarChart>
  );


const Index = (props) => (
  <div>
    {renderLineChart()}

    <h1>Batman TV Shows</h1>
    {props.test[0].totalXP}
    {props.test[1].totalXP}
    {props.test[2].totalXP}
    <ul>
    </ul>
  </div>
)

Index.getInitialProps = async function() {
  const userNames = ['riginding', 'madfist', 'theanswer']
  const fetchData = await Promise.all(userNames.map(user => fetch(`https://codestats.net/api/users/${user}`)))
    console.log('1',fetchData)

  const data = await Promise.all(fetchData.map((userData) => { 
      userData.json()
    }))

    console.log('2', data )

  return {
    test: data.map(x=> {
       return {
           totalXP: x.total_xp,
           name: x.name
       }
    }),
  }
}

export default Index
